<script>
  Alpine.data('product', () => ({
      products:[],
      islogin: false,
  ceklogin(){
      const token = localStorage.getItem('token')
      this.islogin = token ? true : false
      },

  fetchProducts() {
    fetch('https://friendzone-bakery.fly.dev/api/product')
      .then(response => response.json())
      .then(data => {
        this.products = data.data;
      });
  },
  
  }))
  
</script>
